def add_age(x, refdate="2016-06-20") :
  dd=    arrow.get(x, "YYYY-MM-DD")
  dd0=   arrow.get(refdate)
  age1=  int( (dd0 - dd).days /365.25 )
  if ( (age1 > 100) or (age1 < 5 ) ) :
     return 0
  else :
     return age1

